TestAssertion.expression:
let $property_string := 'dimension' let $features_selected := $features[ self::el-cov:ElevationGridCoverage]
let $messages :=
for $feature in $features_selected/gml:domainSet let $envDom := $feature/gml:RectifiedGrid
return if (not(exists($envDom))) then local:addMessage('TR.missingElement', map {'filename': local:filename($feature/..), 'featureType':local-name($feature/..), 'gmlid': string($feature/../@*:id), 'property': $property_string}) else if (count($envDom) > 1) then local:addMessage('TR.wrongMultiplicity', map {'filename': local:filename($feature/..), 'featureType':local-name($feature/..), 'gmlid': string($feature/../@*:id), 'element': $property_string, 'multiplicity': 1, 'count': count($envDom)}) else if (exists($envDom) and (not(exists($envDom/@dimension)) or not($envDom/@dimension = '2'))) then local:addMessage('TR.constraintViolation', map { 'filename': local:filename($feature/..), 'featureType':local-name($feature/..), 'gmlid': string($feature/../@*:id), 'constraint': 'the grid dimension is 2 for an elevation grid coverage', 'additionalInfo': ''}) else ()
return (if ($messages) then 'FAILED' else 'PASSED', local:error-statistics('TR.recordsWithErrors', count(fn:distinct-values($messages//etf:argument[@token='id']/text()))), $messages)

let $property_string := 'domainExtent' let $features_selected := $features[self::el-cov:ElevationGridCoverage]
let $messages :=
for $feature in $features_selected/el-cov:domainExtent/gmd:EX_Extent let $envDom := $feature/gmd:geographicElement
return if(not(exists($envDom))) then local:addMessage('TR.constraintViolation', map { 'filename': local:filename($feature/../..), 'featureType':local-name($feature/../..), 'gmlid': string($feature/../../@*:id), 'constraint': 'the domainExtent is at least populated with a subtype of EX_GeographicExtent', 'additionalInfo': ''}) else ()
return (if ($messages) then 'FAILED' else 'PASSED', local:error-statistics('TR.recordsWithErrors', count(fn:distinct-values($messages//etf:argument[@token='id']/text()))), $messages)

let $property_string := 'origin CRS' let $features_selected := $features[self::el-cov:ElevationGridCoverage]
let $messages :=
for $feature in $features_selected/gml:domainSet/gml:RectifiedGrid/gml:origin let $envDom := $feature/gml:Point
return if (count($envDom) > 1) then local:addMessage('TR.wrongMultiplicity', map {'filename': local:filename($feature/../../..), 'featureType':local-name($feature/../../..), 'gmlid': string($feature/../../../@*:id), 'element': $property_string, 'multiplicity': '0 or 1', 'count': count($envDom)}) else if (exists($envDom) and (not(exists($envDom/@srsName)) or string-length($envDom/@srsName)=0)) then local:addMessage('TR.constraintViolation', map { 'filename': local:filename($feature/../../..), 'featureType':local-name($feature/../../..), 'gmlid': string($feature/../../../@*:id), 'constraint': 'the coordinate reference system used to reference the grid is provided', 'additionalInfo': ''}) else ()
return (if ($messages) then 'FAILED' else 'PASSED', local:error-statistics('TR.recordsWithErrors', count(fn:distinct-values($messages//etf:argument[@token='id']/text()))), $messages)

let $property_string := 'origin dimension' let $features_selected := $features[self::el-cov:ElevationGridCoverage]
let $messages :=
for $feature in $features_selected/gml:domainSet/gml:RectifiedGrid/gml:origin let $envDom := $feature/gml:Point
return if (count($envDom) > 1) then local:addMessage('TR.wrongMultiplicity', map {'filename': local:filename($feature/../../..), 'featureType':local-name($feature/../../..), 'gmlid': string($feature/../../../@*:id), 'element': $property_string, 'multiplicity': '0 or 1', 'count': count($envDom)}) else if (exists($envDom) and (not(exists($envDom/@srsDimension)) or not($envDom/@srsDimension = '2'))) then local:addMessage('TR.constraintViolation', map { 'filename': local:filename($feature/../../..), 'featureType':local-name($feature/../../..), 'gmlid': string($feature/../../../@*:id), 'constraint': ' the origin of the grid is described in two dimensions', 'additionalInfo': ''}) else ()
return (if ($messages) then 'FAILED' else 'PASSED', local:error-statistics('TR.recordsWithErrors', count(fn:distinct-values($messages//etf:argument[@token='id']/text()))), $messages)
