TestAssertion.expression:
let $features := $features[self::plu:OfficialDocumentation]
let $messages :=
	for $feature in $features
		let $legislationCitation := $feature/plu:legislationCitation/@xlink:href
		let $regulationText := $feature/plu:regulationText
		let $planDocument := $feature/plu:planDocument/@xlink:href

		let $validLegislationURL := 
			if (exists($legislationCitation)) then
				if (count($legislationCitation) = 1) then
					let $map := local:check-resource-uri(string($legislationCitation), 30, true())
					let $legislationUri := map:get($map,string($legislationCitation))
					return
						if (($legislationUri = 'notHTTP' or starts-with($legislationUri, 'EXCEPTION') or $legislationUri = 'idNotFound' or matches($legislationUri, '^\d{3}$')) = 0) then $legislationUri
						else()
				else()
			else ()				

		let $validPlanDocumentURL := 
			if (exists($planDocument)) then
				if (count($planDocument) = 1) then
					let $map := local:check-resource-uri(string($planDocument), 30, true())
					let $planDocumentUri := map:get($map,string($planDocument))
					return
						if (($planDocumentUri = 'notHTTP' or starts-with($planDocumentUri, 'EXCEPTION') or $planDocumentUri = 'idNotFound' or matches($planDocumentUri, '^\d{3}$')) = 0) then $planDocumentUri
						else()
				else()
			else ()		


		return
			if(count($legislationCitation) > 1) then
				local:addMessage('TR.wrongMultiplicity',  map {'filename': local:filename($feature), 'featureType':local-name($feature), 'gmlid': string($feature/@*:id), 'element': 'regulationNature', 'multiplicity': 1, 'count': count($legislationCitation)}) 

			else if(count($regulationText) > 1) then
				local:addMessage('TR.wrongMultiplicity',  map {'filename': local:filename($feature), 'featureType':local-name($feature), 'gmlid': string($feature/@*:id), 'element': 'regulationNature', 'multiplicity': 1, 'count': count($regulationText)}) 

			else if(count($planDocument) > 1) then
				local:addMessage('TR.wrongMultiplicity',  map {'filename': local:filename($feature), 'featureType':local-name($feature), 'gmlid': string($feature/@*:id), 'element': 'regulationNature', 'multiplicity': 1, 'count': count($planDocument)}) 

			else if (string-length($legislationCitation) = 0 and string-length($regulationText) = 0 and string-length($planDocument) = 0) then 
				local:addMessage('TR.constraintViolation2',  map {'filename': local:filename($feature), 'featureType':local-name($feature), 'gmlid': string($feature/@*:id), 'constraint': 'At least one of the attributes legislationCitation, regulationText or planDocument is populated with a non-void value.'})

			else if (count($legislationCitation) = 1  and count($validLegislationURL) = 0) then 
				local:addMessage('TR.constraintViolation2',  map {'filename': local:filename($feature), 'featureType':local-name($feature), 'gmlid': string($feature/@*:id), 'constraint': 'The provided URL in xlink:href attribute for legislationCitation must be working.'}) 

			else if (count($planDocument) = 1 and count($validPlanDocumentURL) = 0) then 
				local:addMessage('TR.constraintViolation2',  map {'filename': local:filename($feature), 'featureType':local-name($feature), 'gmlid': string($feature/@*:id), 'constraint': 'The provided URL in xlink:href attribute for planDocument must be working.'}) 

			else ()
return
(if ($messages) then 'FAILED' else 'PASSED',
local:error-statistics('TR.recordsWithErrors', count(fn:distinct-values($messages//etf:argument[@token='id']/text()))),
$messages)
