									if (count(index-of($keywordCharacterString, 'iacs')) > 0 and count(index-of($keywordCharacterString, 'lpis')) = 0 and count(index-of($keywordCharacterString, 'gsaa')) = 0) then
										local:addMessage('TR.keywordError', map {'filename': local:filename($record), 'featureType':local-name($record), 'gmlid': string($record/@*:id), 'value': 'LPIS or GSAA'}) 
									else ()

								return
									if (count($invalidAnchorMain) > 0 and count($invalidCharacterStringMain) > 0) then local:addMessage('TR.keywordError', map {'filename': local:filename($record), 'featureType':local-name($record), 'gmlid': string($record/@*:id), 'value': 'IACS'}) 
									else if((count(index-of($keywordAnchor, 'http://inspire.ec.europa.eu/metadata-codelist/IACSData/iacs')) > 0 and count($invalidAnchorBody) > 0) or (count(index-of($keywordCharacterString, 'iacs')) > 0 and count($invalidCharacterStringBody) > 0)) then local:addMessage('TR.keywordError', map {'filename': local:filename($record), 'featureType':local-name($record), 'gmlid': string($record/@*:id), 'value': 'LPIS or GSAA'})
									else ()
		return 
