return if (count($crsIdentifiers) = 0) then local:addMessage('TR.noCrsIdentifier', map { 'filename': local:filename($record), 'id': $rid }) else for $crs in $crsIdentifiers let $code := $crs/gmd:code let $codeSpace := $crs/gmd:codeSpace return if (not($code) or not(string-length($code/gco:CharacterString/text()) != 0 or string-length($code/gmx:Anchor/text()) != 0)) then local:addMessage('TR.noCrsCode', map { 'filename': local:filename($record), 'id': $rid }) else if ($codeSpace and not(string-length($codeSpace/gco:CharacterString/text()) != 0 or string-length($codeSpace/gmx:Anchor/text()) != 0)) then local:addMessage('TR.noCrsCodeSpace', map { 'filename': local:filename($record), 'id': $rid }) else ()
