				let $codeNumberOpt2 := fn:substring($code/gmx:Anchor/@xlink:href, fn:string-length($code/gmx:Anchor/@xlink:href)-4+1)
	 			return
				if ($codeNumberOpt1 = $epsgCodes or $codeNumberOpt2 = $epsgCodes) then
