						local:addMessage('TR.invalidCrsCode',  map { 'filename': local:filename($record), 'id': $rid })
					else if($codeSpace) then
						local:addMessage('TR.invalidCrsCode',  map { 'filename': local:filename($record), 'id': $rid })
					else ()
				else ()
