	testRunner.testCase.testSuite.project.testSuites['Language_Requirements'].testCases['at-32-language-getrecords-exception'].testSteps['manual-check-cql_text'].setDisabled(false);
}

else if(filter_language_supported){
	testRunner.testCase.testSuite.project.testSuites['Discovery_Service_Queryable'].testCases['at-20-query-language-parameter'].testSteps['http-getrecords-language-filter'].setDisabled(false);
	testRunner.testCase.testSuite.project.testSuites['Language_Requirements'].testCases['at-31-language-getrecords-lang-filter'].testSteps['manual-check-filter'].setDisabled(false);
	testRunner.testCase.testSuite.project.testSuites['Language_Requirements'].testCases['at-32-language-getrecords-exception'].testSteps['manual-check-filter'].setDisabled(false);
