def tc = testRunner.testCase.testSuite.project.testSuites["Discovery_Service_Operations"].testCases["at-12-discover-resource-matching-query"];
def response = tc.getTestStepByName("http-getrecords").getPropertyValue("Response");
def getRecordsResponse = new XmlSlurper(false, true).parseText(response);

if(getRecordsResponse.SearchResults.MD_Metadata.size() != getRecordsResponse.SearchResults.children().size()){
	throw new TranslatableAssertionError('TR.invalidGetRecordsResponse');
}
getRecordsResponse.SearchResults.children().each{it ->
	def fileIdentifier = it.fileIdentifier.CharacterString;
	def metadataType = it.hierarchyLevel.MD_ScopeCode."@codeListValue".toString();
	// Hierarchy
	if(!(metadataType == 'dataset' || metadataType == 'series' || metadataType == 'service')){
		def description = 'The codeListValue attribute of hierarchyLevel/MD_ScopeCode shall be dataset, series or service'
		String[] assertParams = ['identifier', fileIdentifier.toString(), 'description', description.toString(), 'found', metadataType];
		throw new TranslatableAssertionError('TR.recordNotConformantToInspire', assertParams);
	}
}

