languages_to_request << capabilities.OperationsMetadata.ExtendedCapabilities.SupportedLanguages.DefaultLanguage.Language;
for (i in capabilities.OperationsMetadata.ExtendedCapabilities.SupportedLanguages.SupportedLanguage.Language){
	languages_to_request << i;
}
languages_to_request << wrong_language;

def ts = testRunner.testCase.testSuite.project.testSuites["Language_Requirements"].testCases["at-27-language-response-value"].getTestStepByName('http-request-languaged');
for (i in languages_to_request){
	testRunner.testCase.setPropertyValue('language', i.toString());
	ts.setDisabled(false);
	ts.run(testRunner, context);
	def validContentType = false;
	for (j in ts.testRequest.response.responseHeaders.get('Content-Type')){
		if (j.contains('xml')){
			validContentType = true;
		}
	}
	if(!validContentType){
		throw new TranslatableAssertionError("TR.notXMLResponse");
	}
	def capabilitiesLanguaged = new XmlSlurper(false, true).parseText(ts.getPropertyValue("Response"));
	if(i == wrong_language && defaultLanguage.toString() != capabilitiesLanguaged.OperationsMetadata.ExtendedCapabilities.ResponseLanguage.Language.toString()){
		throw new TranslatableAssertionError("TR.responseLanguageDifferentThanRequested");
	}
	else if(i != wrong_language && i.toString() != capabilitiesLanguaged.OperationsMetadata.ExtendedCapabilities.ResponseLanguage.Language.toString()){
		throw new TranslatableAssertionError("TR.responseLanguageDifferentThanRequested");
	}
}
ts.setDisabled(true);

import de.interactive_instruments.etf.suim.TranslatableAssertionError;
def tc = testRunner.testCase.testSuite.project.testSuites["Initialize"].testCases["initial-testcase"];
def response = tc.getTestStepByName("http-request").getPropertyValue("Response");
def capabilities = new XmlSlurper(false, true).parseText(response);

def responseLanguage = capabilities.OperationsMetadata.ExtendedCapabilities.ResponseLanguage.Language;
def defaultLanguage = capabilities.OperationsMetadata.ExtendedCapabilities.SupportedLanguages.DefaultLanguage.Language;
def supportedLanguage = capabilities.OperationsMetadata.ExtendedCapabilities.SupportedLanguages.SupportedLanguage.Language;

if(defaultLanguage.size() != 1){
	String[] assertParams = ['element', 'DefaultLanguage'];
	throw new TranslatableAssertionError('TR.multiplicityShallBeOne', assertParams);
}

import de.interactive_instruments.etf.suim.*

def tsGetObservation = testRunner.testCase.testSuite.project.testSuites["Language_Requirements"].testCases["at-29-extended-capabilities-schema-valid"].getTestStepByName('http-request-xml-schema-valid');
tsGetObservation.setDisabled(false);
try{
	tsGetObservation.run(testRunner, context);
}
catch (Exception e){
	String[] assertParams = ['error', e];
	throw new TranslatableAssertionError('TR.invalidSchema', assertParams);
}

def response = tsGetObservation.getPropertyValue("Response");
def capabilities = new XmlSlurper(false, true).parseText(response);
def schemaName = 'inspire_ds.xsd';
if(!(capabilities.@'xsi:schemaLocation'.toString().contains(schemaName))){
	String[] assertParams = ['error', 'Missing schema location reference to ' + schemaName];
	throw new TranslatableAssertionError('TR.invalidSchema', assertParams);
}

def assertions = tsGetObservation.getAssertionList();
assertions.each{
	if(it.name.toString() == 'Simple Schema Validator' && it.status.toString() == 'FAILED'){
		String[] assertParams = ['error', it.errors.toString()];
		throw new TranslatableAssertionError('TR.invalidSchema', assertParams);
	}
}
tsGetObservation.setDisabled(true);

