		if(abst.toString().length() == 0){
			String[] assertParams = ["layerid", layer.Identifier.toString()];
			throw new TranslatableAssertionError("TR.emptyAbstract", assertParams);
			//Msg: The '{layerid}' layer Abstract is empty.
		}
	}
